mapper.select:
select * from ooi_interface_info
where
data_state = 'U'

    and interface_id = #{interfaceId}


    and interface_type = #{interfaceType}


    and interface_deal_type = #{interfaceDealType}


    and interface_cycle = #{interfaceCycle}


    and (interface_id = #{fuzzyQueryInfo} or interface_name = #{fuzzyQueryInfo} or interface_desc = #{fuzzyQueryInfo})

mapper.insert:
insert into ooi_interface_info (
interface_id,
interface_type,
interface_deal_type,
interface_cycle,
interface_loca_path,
interface_remote_path,
interface_run_time,
file_name
) values (
#{interfaceId},
#{interfaceType},
#{interfaceDealType},
#{interfaceCycle},
#{interfaceLocaPath}
#{interfaceRemotePath}
#{interfaceRunTime}
#{fileName}
);
